    private void BtnResActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnResActionPerformed
        if (Txt01.getText().isEmpty()|| Txt02.getText().isEmpty()){
            TxtResultado.setText("Valores vacios");
        }else{
            TxtResultado.setText(cal.restar(Txt01.getText(), Txt02.getText()));
        }
    }//GEN-LAST:event_BtnResActionPerformed

    private void BtnSumActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnSumActionPerformed
        if (Txt01.getText().isEmpty()|| Txt02.getText().isEmpty()){
            TxtResultado.setText("Valores vacios");
        }else{
            TxtResultado.setText(cal.sumar(Txt01.getText(), Txt02.getText()));
        }
    }//GEN-LAST:event_BtnSumActionPerformed

    private void BtnMulActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnMulActionPerformed
        if (Txt01.getText().isEmpty()|| Txt02.getText().isEmpty()){
            TxtResultado.setText("Valores vacios");
        }else{
            TxtResultado.setText(cal.multiplicar(Txt01.getText(), Txt02.getText()));
        }
    }//GEN-LAST:event_BtnMulActionPerformed

    private void BtnDivActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnDivActionPerformed
        if (Txt01.getText().isEmpty()|| Txt02.getText().isEmpty()){
            TxtResultado.setText("Valores vacios");
        }else{
            TxtResultado.setText(cal.dividir(Txt01.getText(), Txt02.getText()));
        }
    }//GEN-LAST:event_BtnDivActionPerformed

